const Publication = ({title, publicationDate, url, children}) => {
    if (!Boolean(children)) {
        return (<></>)
    }

    return (
        <PublicationDiv>
            <PublicationTitle>{title}</PublicationTitle>
            <PublicationDate>Publicado em: {publicationDate}</PublicationDate>
            <PublicationText>{children}</PublicationText>
            <DivBotao>
                <BotaoTechTudo href={url} target="_blank">Ver no&nbsp;<LogoTechTudo/></BotaoTechTudo>
            </DivBotao>
        </PublicationDiv>
    )

}

export default Publication;